import { createSlice } from '@reduxjs/toolkit'


createSlice ({
    name: 'ui',
    intialValue: { cartVisible : false },
    reducers:{
         toggle(state){
            state.cartVisible = !state.cartVisible;
         }
    }
});


export default createSlice.ui;
