import { createSlice } from '@reduxjs/toolkit'


const uiSlice = createSlice ({
    name: 'ui',
    intialValue: { cartVisible : false },
    reducers:{
         toggle(state){
            state.cartVisible = !state.cartVisible;
         }
    }
});

export const uiSliceActions = uiSlice.actions;
export default uiSlice;
